public:
int strToInt(string str) {
if (str.size() == 0) return 0;
str += 'g';
int idx = 0;
long anw = 0;
bool ff = false;
while (str[idx] == ' ') idx++;
if (str[idx] == '-') ff = true, idx++;
if (str[idx] == '+') idx++;
for (int i = idx; i &lt; str.size(); ++i) {
if (str[i] &lt; '0' || str[i] &gt; '9')
break;
anw *= 10;
anw += str[i] - '0';
if (anw &gt; INT_MAX &amp;&amp; !ff) return INT_MAX;
if (ff &amp;&amp; (0 - anw) &lt; INT_MIN) return INT_MIN;
}
if (ff) anw = 0 - anw;
return anw;
}
};
