Lakes <p>这个题在于剪枝，有的不用再dfs了，不然超时。假如（1，1）和（1，2）联通，dfs（1，1）和dfs（1，2）是一个结果。 <pre><code>#include &lt;cstring&gt;
#include "iostream"
using namespace std;
const int N = 1010;
int gra[N][N];
int n, m;
int total;
int dx[4] = {0, 0, -1, 1};
int dy[4] = {1, -1, 0, 0};
bool visited[N][N];
int dfs(int a, int b) {
visited[a][b] = true;
if (gra[a][b] == 0) return 0;
int anw = gra[a][b];
for (int i = 0; i &lt; 4; i++) {
int nx = a + dx[i];
int ny = b + dy[i];
if (nx &gt;= 1 &amp;&amp; nx &lt;= n &amp;&amp; ny &gt;= 1 &amp;&amp; ny &lt;= m
&amp;&amp; gra[nx][ny] &gt; 0 &amp;&amp; visited[nx][ny] == false) { anw += dfs(nx, ny); }
}
return anw;
}
void solve() {
int fin = 0;
cin &gt;&gt; n &gt;&gt; m;
for (int i = 1; i &lt;= n; i++)
for (int j = 1; j &lt;= m; j++)
cin &gt;&gt; gra[i][j];
memset(visited, 0, sizeof visited);
for (int i = 1; i &lt;= n; i++)
for (int j = 1; j &lt;= m; j++) {
if (gra[i][j] != 0 &amp;&amp; visited[i][j] == false) {
fin = max(dfs(i, j), fin);
}
}
cout &lt;&lt; fin &lt;&lt; endl;
}
int main() {
cin &gt;&gt; total;
while (total--)
solve();
return 0;
}
</code></pre></p>

&amp; <pre><code>#include "iostream"
#include "vector"
using namespace std;
void merge(vector&lt;int&gt; &amp;nums, vector&lt;int&gt; &amp;copy, int l, int r) {
if (l == r) {
copy[l] = nums[l];
return;
}
int mid = l + r &gt;&gt; 1;
merge(copy, nums, l, mid);
merge(copy, nums, mid + 1, r);
int i = l, j = mid + 1, idx = l;
while (i &lt;= mid &amp;&amp; j &lt;= r) {
if (nums[i] &lt; nums[j]) copy[idx++] = nums[i++];
else copy[idx++] = nums[j++];
}
while (i &lt;= mid)
copy[idx++] = nums[i++];
while (j&lt;=r)
copy[idx++] = nums[j++];
}
void mergesort(vector&lt;int&gt; &amp;nums) {
if (nums.size() &lt;= 1) return;
vector&lt;int&gt; copy = nums;
merge(nums, copy, 0, nums.size() - 1);
nums=copy;
}
int main() {
vector&lt;int&gt; nums;
int n;
cin &gt;&gt; n;
nums.resize(n);
for (int i = 0; i &lt; n; ++i) {
cin &gt;&gt; nums[i];
}
mergesort(nums);
for (auto x: nums)
cout &lt;&lt; x &lt;&lt; ' ';
return 0;
}
